import React from 'react'
import dogs from '../dogsdata'

const DogDetails = (props) => {
    const selectedDogId = props.match.params.writtenDogId;
    const showDogs = dogs.filter((dog) => dog.id === selectedDogId)
    return (
        <div>

            <ul>
            <h1>selectedDog: {
                dogs.forEach((dog) => { if(selectedDogId === dog.id){
                    return dog.name 
                }}
                )}</h1>
                {
                    showDogs.map((dog) => {
                        return <li>{dog.description}
                                    <li>
                                        {dog.age}
                                    </li>
                                    <li>
                                        {dog.breed}
                                    </li>
                                </li>
                    })
                }
            </ul>
        </div>
    )
}

export default DogDetails
