import React from 'react'
import dogs from '../dogsdata'

const DogDetails = (props) => {
    const selectedDog = props.match.params.writtenDog;
    const showDogs = dogs.filter((dog) => dog.id === selectedDog)
    const myDog = dogs.find((dog)=> dog.id=== selectedDog)

    return (
        <div>
            selectedDog: {showDogs.map((dog)=> {return <h1> {dog.name}</h1>
                })}
            {/*
                dogs.map((dog) => { if(selectedDog === dog.id){
                    return dog.name 
                }}
                )
           */}

            second: {
                 myDog.name
            }
            <ul>
                {
                    showDogs.map((dog) => {
                        return <li>{dog.description}
                            <li>
                                {dog.age}
                            </li>
                            <li>
                                {dog.breed}
                            </li>
                        </li>
                    })
                }
            </ul>
        </div>
    )
}

export default DogDetails
